function createElement(type, config, ...args){
    const props = Object.assign({}, config);               //props要复制给另一个对象
    const hasChildren = args.length > 0
    const rawChildren = hasChildren ? [].concat(...args) : [];
    props.children = rawChildren
                  .filter(child => child != null && child !== false)
                  .map(child => child instanceof Object? child :createTextElement(child))  //typeof child === 'string' ? createTextElement(child) :child 
    return {type, props};
}
//规范文本节点的数据结构，若为文本节点，返回值如{type:'TEXT_ELEMENT', props: {nodeValue: 'hello world'}}
function createTextElement(text){
  return createElement(TEXT_ELEMENT,{ nodeValue: text })
 }
 export default createElement

